package patternsInNumbers;

public class patternThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while(i<=5)
		{
			
			int j=5;
			while(j>=i)
			{
				
				System.out.print(j);
				j=j-1;
			}
			System.out.println();
			i=i+1;
			
	}
		
		
	}

}
